  </div>
</template>

<script>
import axios from 'axios'
const request = axios.create({
  baseURL: 'http://localhost:8080/api'
})
export default({
  name: 'paper',
  data() {
    return {
      paperList: [],
      subject: '计算机组成原理'
    }
  },
  methods: {
    nextpaper(i) {
      this.paperList[i] = '正在生成试卷和答案......'
      this.getpaper(i)
    },
    getpaper(i) {
      request({
        method: 'POST',
        url: '/multiQuestion',
        data: {
          subject: this.subject
        },
        transformRequest: [function (data) {
          let ret = ''
          for (let it in data) {
            ret += encodeURIComponent(it) + '=' + encodeURIComponent(data[it]) + '&'
          }
          return ret
        }],
        headers: {'Content-Type': 'application/x-www-form-urlencoded'}
      }).then((res) => {
        console.log(res)
        this.paperList[i] = res.data.paper
        this.paperList[i] += '\n\n\n\n答案解析：\n' + res.data.answer + '\n\n\n\n'
      })
    },
    copyText(i) {
      this.$copyText(this.paperList[i]).then((res) => {
        this.$message.success('复制成功！')
      })
    }
  }
})
</script>

